import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../assets/logo.jpg';

const Navbar = () => {
    const [menuOpen, setMenuOpen] = useState(false);

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.href = "/*";
    };

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <nav className="navbar">
            <div className="navbar-left">
                <img src={logo} alt="Campus Logo" className="navbar-logo" />
                <span className="navbar-title">Campus Navigation</span>
            </div>

            <button className="menu-btn" onClick={toggleMenu}>
                â˜°
            </button>

            <div className={`navbar-right ${menuOpen ? 'active' : ''}`}>
                <Link to="/map" className="nav-link" onClick={() => setMenuOpen(false)}>Map</Link>
                <Link to="/Info" className="nav-link" onClick={() => setMenuOpen(false)}>Info</Link>
                <Link to="/about" className="nav-link" onClick={() => setMenuOpen(false)}>About</Link>
                <Link to="/photos" className="nav-link" onClick={() => setMenuOpen(false)}>Gallery</Link>
                <button
                    onClick={handleLogout}
                    className="nav-btn logout-btn"
                >
                    Logout
                </button>
            </div>
        </nav>
    );
};

export default Navbar;
