        const bbox = L.latLngBounds(
          L.latLng(loc.geometry.viewport.northeast),
          L.latLng(loc.geometry.viewport.southwest)
        );
        results[i] = {
          name: loc.formatted_address,
          bbox: bbox,
          center: center,
          properties: loc.address_components
        };
      }
    }

    return results;
  }
}

